#### `includes/db.php`
```php
<?php
require_once 'config.php';

class Database {
    private $pdo;
    private static $instance = null;

    private function __construct() {
        try {
            $dsn = "mysql:host=" . DB_HOST . ";dbname=" . DB_NAME . ";charset=utf8mb4";
            $options = [
                PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
                PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
                PDO::ATTR_EMULATE_PREPARES => false,
                PDO::ATTR_PERSISTENT => true,
                PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES 'utf8mb4' COLLATE 'utf8mb4_unicode_ci'"
            ];

            $this->pdo = new PDO($dsn, DB_USER, DB_PASS, $options);
        } catch (PDOException $e) {
            error_log("Database connection error: " . $e->getMessage());
            die("Database connection failed. Please try again later.");
        }
    }

    public static function getInstance() {
        if (self::$instance === null) {
            self::$instance = new Database();
        }
        return self::$instance;
    }

    public function getConnection() {
        return $this->pdo;
    }

    // Prevent cloning
    private function __clone() {}

    // Prevent unserializing
    public function __wakeup() {}
}

// Initialize database connection
$pdo = Database::getInstance()->getConnection();
```

### 2. Authentication System

#### `includes/auth.php`
```php
<?php
require_once 'db.php';
require_once 'functions.php';
require_once 'compliance.php';

session_set_cookie_params([
    'lifetime' => SESSION_LIFETIME,
    'path' => '/',
    'domain' => parse_url(APP_URL, PHP_URL_HOST),
    'secure' => true,
    'httponly' => true,
    'samesite' => 'Strict'
]);
session_start();

// Regenerate session ID periodically for security
if (!isset($_SESSION['last_regeneration'])) {
    session_regenerate_id(true);
    $_SESSION['last_regeneration'] = time();
} elseif (time() - $_SESSION['last_regeneration'] > 300) {
    session_regenerate_id(true);
    $_SESSION['last_regeneration'] = time();
}

// Check session timeout
if (isset($_SESSION['last_activity']) && (time() - $_SESSION['last_activity'] > SESSION_LIFETIME)) {
    logoutUser();
}

// Update last activity time stamp
$_SESSION['last_activity'] = time();

// CSRF Protection
if (empty($_SESSION['csrf_token'])) {
    $_SESSION['csrf_token'] = bin2hex(random_bytes(32));
}

function checkCsrfToken($token) {
    if (empty($token) || empty($_SESSION['csrf_token'])) {
        return false;
    }
    return hash_equals($_SESSION['csrf_token'], $token);
}

function loginUser($email, $password, $role = 'applicant') {
    global $pdo;

    // Input validation
    if (empty($email) || empty($password)) {
        return ['success' => false, 'message' => 'Email and password are required'];
    }

    // Sanitize email
    $email = filter_var($email, FILTER_SANITIZE_EMAIL);

    // Determine table based on role
    $table = ($role === 'admin') ? 'admins' : 'applicants';

    try {
        $stmt = $pdo->prepare("SELECT * FROM $table WHERE email = ?");
        $stmt->execute([$email]);
        $user = $stmt->fetch();

        if (!$user) {
            return ['success' => false, 'message' => 'Invalid email or password'];
        }

        // Verify password
        if (!password_verify($password, $user['password'])) {
            return ['success' => false, 'message' => 'Invalid email or password'];
        }

        // Check if account is active
        if ($role === 'applicant' && $user['status'] !== 'active') {
            return ['success' => false, 'message' => 'Your account is not active. Please contact support.'];
        }

        // Store user in session
        $_SESSION['user'] = [
            'id' => $user['id'],
            'email' => $user['email'],
            'name' => $user['name'],
            'role' => $role,
            'authenticated' => true
        ];

        // Log login activity for compliance
        logComplianceEvent('LOGIN_SUCCESS', $user['id'], $role);

        return ['success' => true, 'message' => 'Login successful'];
    } catch (PDOException $e) {
        error_log("Database error during login: " . $e->getMessage());
        return ['success' => false, 'message' => 'Database error. Please try again later.'];
    }
}

function logoutUser() {
    // Log logout activity for compliance
    if (isset($_SESSION['user'])) {
        logComplianceEvent('LOGOUT', $_SESSION['user']['id'], $_SESSION['user']['role']);
    }

    // Destroy session
    $_SESSION = [];
    session_unset();
    session_destroy();

    // Delete session cookie
    if (ini_get("session.use_cookies")) {
        $params = session_get_cookie_params();
        setcookie(session_name(), '', time() - 42000,
            $params["path"], $params["domain"],
            $params["secure"], $params["httponly"]
        );
    }

    // Redirect to login page
    header('Location: ' . ($role === 'admin' ? '../admin/login.php' : 'login.php'));
    exit();
}

function requireLogin($role = null) {
    if (!isset($_SESSION['user']) || !$_SESSION['user']['authenticated']) {
        header('Location: ' . ($role === 'admin' ? '../admin/login.php' : 'login.php'));
        exit();
    }

    if ($role && $_SESSION['user']['role'] !== $role) {
        header('HTTP/1.0 403 Forbidden');
        exit('Access denied');
    }
}

function isUserLoggedIn() {
    return isset($_SESSION['user']) && $_SESSION['user']['authenticated'];
}

function getCurrentUser() {
    return isUserLoggedIn() ? $_SESSION['user'] : null;
}

function hashPassword($password) {
    if (strlen($password) < PASSWORD_MIN_LENGTH) {
        return false;
    }
    return password_hash($password, PASSWORD_DEFAULT);
}

// Password policy enforcement
function validatePassword($password) {
    $errors = [];

    if (strlen($password) < PASSWORD_MIN_LENGTH) {
        $errors[] = "Password must be at least " . PASSWORD_MIN_LENGTH . " characters long";
    }

    if (!preg_match('/[A-Z]/', $password)) {
        $errors[] = "Password must contain at least one uppercase letter";
    }

    if (!preg_match('/[a-z]/', $password)) {
        $errors[] = "Password must contain at least one lowercase letter";
    }

    if (!preg_match('/[0-9]/', $password)) {
        $errors[] = "Password must contain at least one number";
    }

    if (!preg_match('/[^A-Za-z0-9]/', $password)) {
        $errors[] = "Password must contain at least one special character";
    }

    return $errors;
}
```

### 3. Compliance System

#### `includes/compliance.php`
```php
<?php
require_once 'db.php';

// Log compliance events for audit trail
function logComplianceEvent($event_type, $user_id, $role, $details = null) {
    global $pdo;

    try {
        $ip = $_SERVER['REMOTE_ADDR'] ?? 'unknown';
        $user_agent = $_SERVER['HTTP_USER_AGENT'] ?? 'unknown';

        $stmt = $pdo->prepare("INSERT INTO compliance_logs (event_type, user_id, role, ip_address, user_agent, details, created_at) 
                              VALUES (?, ?, ?, ?, ?, ?, NOW())");
        $stmt->execute([
            $event_type,
            $user_id,
            $role,
            $ip,
            $user_agent,
            $details ? json_encode($details) : null
        ]);

        return true;
    } catch (PDOException $e) {
        error_log("Compliance logging error: " . $e->getMessage());
        return false;
    }
}

// Check if user is eligible based on CSIR guidelines
function checkCSIREligibility($applicant_data, $vacancy_data) {
    $errors = [];

    // 1. Age limit check (CSIR Rule 5.2)
    $dob = new DateTime($applicant_data['dob']);
    $today = new DateTime();
    $age = $today->diff($dob)->y;

    // Age limits vary by post type
    $max_age = 35; // Default for Scientist posts
    if (strpos($vacancy_data['post_title'], 'Scientist') !== false) {
        $max_age = 35;
    } elseif (strpos($vacancy_data['post_title'], 'Technician') !== false) {
        $max_age = 30;
    } elseif (strpos($vacancy_data['post_title'], 'Technical Assistant') !== false) {
        $max_age = 27;
    }

    if ($age > $max_age) {
        $errors[] = "Age ($age years) exceeds maximum limit of $max_age years for this position (CSIR Recruitment Rule 5.2)";
    }

    // 2. Educational qualification check
    $required_qualification = $vacancy_data['minimum_qualification'];
    $has_qualification = false;

    foreach ($applicant_data['education'] as $edu) {
        if (stripos($edu['degree'], $required_qualification) !== false) {
            $has_qualification = true;
            break;
        }
    }

    if (!$has_qualification) {
        $errors[] = "Minimum educational qualification ($required_qualification) not met (CSIR Recruitment Rule 3.1)";
    }

    // 3. Experience check (if applicable)
    if (!empty($vacancy_data['experience_required'])) {
        $total_experience = calculateTotalExperience($applicant_data['experience']);
        if ($total_experience < $vacancy_data['experience_required']) {
            $errors[] = "Required experience of {$vacancy_data['experience_required']} years not met. You have $total_experience years.";
        }
    }

    // 4. Reservation category validation
    if (!empty($applicant_data['reservation_category'])) {
        if ($applicant_data['reservation_category'] !== 'UR' && empty($applicant_data['reservation_certificate'])) {
            $errors[] = "Reservation category selected but no valid certificate uploaded (CSIR Recruitment Rule 7.3)";
        }
    }

    return $errors;
}

// Calculate total work experience
function calculateTotalExperience($experience_data) {
    $total_months = 0;

    foreach ($experience_data as $exp) {
        $start = new DateTime($exp['start_date']);
        $end = !empty($exp['end_date']) ? new DateTime($exp['end_date']) : new DateTime();

        $interval = $start->diff($end);
        $total_months += ($interval->y * 12) + $interval->m;
    }

    return round($total_months / 12, 1); // Return in years
}

// GIGW Compliance Check
function checkGIGWCompliance() {
    $issues = [];

    // Check HTTPS
    if (!isset($_SERVER['HTTPS']) || $_SERVER['HTTPS'] !== 'on') {
        $issues[] = "Website not served over HTTPS (GIGW Section 4.2.1)";
    }

    // Check accessibility
    $contrast_ratio = getContrastRatio('#ffffff', '#000000'); // Should be higher than 4.5:1
    if ($contrast_ratio < 4.5) {
        $issues[] = "Text contrast ratio does not meet WCAG 2.0 AA standards (GIGW Section 3.4.2)";
    }

    // Check privacy policy
    if (!file_exists('../privacy_policy.html')) {
        $issues[] = "Privacy policy document not found (GIGW Section 5.1)";
    }

    return $issues;
}

// Calculate color contrast ratio
function getContrastRatio($color1, $color2) {
    // Implementation of contrast ratio calculation
    // Returns ratio like 4.5, 7.0, etc.
    return 21.0; // Placeholder - actual implementation would calculate this
}

// Data retention check
function checkDataRetention() {
    global $pdo;

    try {
        $stmt = $pdo->prepare("SELECT id, created_at FROM applications WHERE status = 'rejected' AND created_at < NOW() - INTERVAL ? YEAR");
        $stmt->execute([DATA_RETENTION_PERIOD]);
        $applications = $stmt->fetchAll();

        return count($applications);
    } catch (PDOException $e) {
        error_log("Data retention check error: " . $e->getMessage());
        return 0;
    }
