<?php
require_once '../includes/auth.php';
require_once '../includes/functions.php';

// If already logged in, redirect to dashboard
if (isUserLoggedIn() && $_SESSION['user']['role'] === 'applicant') {
    header('Location: dashboard.php');
    exit();
}

// Handle registration form submission
$errors = [];
$success = false;

if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $csrf_valid = checkCsrfToken($_POST['csrf_token'] ?? '');

    if (!$csrf_valid) {
        $errors[] = "Invalid request token. Please try again.";
    } else {
        $name = $_POST['name'] ?? '';
        $email = $_POST['email'] ?? '';
        $phone = $_POST['phone'] ?? '';
        $password = $_POST['password'] ?? '';
        $confirm_password = $_POST['confirm_password'] ?? '';

        // Validation
        if (empty($name)) $errors[] = "Name is required";
        if (empty($email)) {
            $errors[] = "Email is required";
        } elseif (!filter_var($email, FILTER_VALIDATE_EMAIL)) {
            $errors[] = "Invalid email format";
        }

        if (empty($phone)) {
            $errors[] = "Phone number is required";
        } elseif (!preg_match('/^[6-9]\d{9}$/', $phone)) {
            $errors[] = "Invalid Indian phone number";
        }

        if (empty($password)) {
            $errors[] = "Password is required";
        } else {
            $password_errors = validatePassword($password);
            if (!empty($password_errors)) {
                $errors = array_merge($errors, $password_errors);
            }

            if ($password !== $confirm_password) {
                $errors[] = "Passwords do not match";
            }
        }

        // Check if email already exists
        if (empty($errors)) {
            global $pdo;
            try {
                $stmt = $pdo->prepare("SELECT id FROM applicants WHERE email = ?");
                $stmt->execute([$email]);
                if ($stmt->fetch()) {
                    $errors[] = "Email already registered";
                }
            } catch (PDOException $e) {
                error_log("Database error during registration check: " . $e->getMessage());
                $errors[] = "Database error. Please try again later.";
            }
        }

        // If no errors, create account
        if (empty($errors)) {
            $hashed_password = hashPassword($password);
            $token = bin2hex(random_bytes(16));

            try {
                $pdo->beginTransaction();

                // Create applicant
                $stmt = $pdo->prepare("INSERT INTO applicants (name, email, phone, password, status, created_at) 
                                      VALUES (?, ?, ?, ?, 'pending', NOW())");
                $stmt->execute([$name, $email, $phone, $hashed_password]);
                $applicant_id = $pdo->lastInsertId();

                // Create verification token
                $stmt = $pdo->prepare("INSERT INTO verification_tokens (applicant_id, token, created_at) 
                                      VALUES (?, ?, NOW())");
                $stmt->execute([$applicant_id, $token]);

                $pdo->commit();

                // Send verification email
                $verification_link = APP_URL . "/applicant/verify.php?token=$token";
                $email_subject = "Verify Your CSIR-SERC Recruitment Account";
                $email_body = "Hello $name,\n\nPlease click the link below to verify your email address:\n\n$verification_link\n\nThis link will expire in 24 hours.\n\nThank you,\nCSIR-SERC Recruitment Team";

                sendEmail($email, $email_subject, $email_body);

                $success = true;
            } catch (PDOException $e) {
                $pdo->rollBack();
                error_log("Database error during registration: " . $e->getMessage());
                $errors[] = "Database error. Please try again later.";
            }
        }
    }
}
