### 7. PDF Generation System

#### `includes/pdf_generator.php`
```php
<?php
require_once 'config.php';
require_once 'db.php';
require_once 'functions.php';

// Generate PDF application form
function generateApplicationPDF($application_id) {
    global $pdo;

    try {
        // Get application data
        $stmt = $pdo->prepare("SELECT * FROM applications 
                              WHERE id = ?");
        $stmt->execute([$application_id]);
        $application = $stmt->fetch();

        if (!$application) {
            throw new Exception("Application not found");
        }

        // Get applicant data
        $stmt = $pdo->prepare("SELECT * FROM applicants 
                              WHERE id = ?");
        $stmt->execute([$application['applicant_id']]);
        $applicant = $stmt->fetch();

        // Get vacancy data
        $stmt = $pdo->prepare("SELECT v.*, p.title as post_title, l.name as lab_name, 
                              pr.name as project_name, pr.description as project_description
                              FROM vacancies v
                              JOIN posts p ON v.post_id = p.id
                              JOIN labs l ON v.lab_id = l.id
                              LEFT JOIN projects pr ON v.project_id = pr.id
                              WHERE v.id = ?");
        $stmt->execute([$application['vacancy_id']]);
        $vacancy = $stmt->fetch();

        // Get education data
        $stmt = $pdo->prepare("SELECT * FROM education 
                              WHERE applicant_id = ?
                              ORDER BY year DESC");
        $stmt->execute([$application['applicant_id']]);
        $education = $stmt->fetchAll();

        // Get publications data
        $stmt = $pdo->prepare("SELECT * FROM publications 
                              WHERE applicant_id = ?
                              ORDER BY year DESC");
        $stmt->execute([$application['applicant_id']]);
        $publications = $stmt->fetchAll();

        // Get experience data
        $stmt = $pdo->prepare("SELECT * FROM experience 
                              WHERE applicant_id = ?
                              ORDER BY start_date DESC");
        $stmt->execute([$application['applicant_id']]);
        $experience = $stmt->fetchAll();

        // Get documents
        $stmt = $pdo->prepare("SELECT * FROM documents 
                              WHERE application_id = ?");
        $stmt->execute([$application_id]);
        $documents = $stmt->fetchAll();

        // Prepare HTML for PDF
        $html = generatePDFHTML($application, $applicant, $vacancy, $education, $publications, $experience, $documents);

        // Generate PDF using Dompdf
        require_once 'vendor/autoload.php';
        use Dompdf\Dompdf;
        use Dompdf\Options;

        $options = new Options();
        $options->set('isRemoteEnabled', true);
        $options->set('isHtml5ParserEnabled', true);
        $options->set('defaultFont', 'dejavu sans');

        $dompdf = new Dompdf($options);
        $dompdf->loadHtml($html);
        $dompdf->setPaper('A4', 'portrait');
        $dompdf->render();

        // Save PDF to file
        $pdf_content = $dompdf->output();
        $pdf_filename = 'application_' . $application_id . '_' . time() . '.pdf';
        $pdf_path = 'uploads/applications/' . $pdf_filename;

        file_put_contents($pdf_path, $pdf_content);

        // Update application record with PDF path
        $stmt = $pdo->prepare("UPDATE applications SET pdf_path = ? WHERE id = ?");
        $stmt->execute([$pdf_path, $application_id]);

        return $pdf_path;
    } catch (Exception $e) {
        error_log("PDF generation error: " . $e->getMessage());
        return false;
    }
}

