// Send Telegram notification
function sendTelegramNotification($message) {
    if (empty(TELEGRAM_BOT_TOKEN) || empty(TELEGRAM_CHAT_ID)) {
        error_log("Telegram not configured");
        return false;
    }

    try {
        $url = "https://api.telegram.org/bot" . TELEGRAM_BOT_TOKEN . "/sendMessage";
        $data = [
            'chat_id' => TELEGRAM_CHAT_ID,
            'text' => " *CSIR-SERC Recruitment Alert*\n\n" . $message,
            'parse_mode' => 'Markdown'
        ];

        $options = [
            'http' => [
                'header'  => "Content-type: application/x-www-form-urlencoded\r\n",
                'method'  => 'POST',
                'content' => http_build_query($data)
            ]
        ];

        $context  = stream_context_create($options);
        $result = file_get_contents($url, false, $context);

        $response = json_decode($result, true);

        if ($response && $response['ok']) {
            logComplianceEvent('TELEGRAM_SENT', 0, 'system', [
                'message' => $message,
                'success' => true
            ]);
            return true;
        } else {
            error_log("Telegram API error: " . ($response['description'] ?? 'Unknown error'));
            logComplianceEvent('TELEGRAM_FAILED', 0, 'system', [
                'message' => $message,
                'error' => $response['description'] ?? 'Unknown error'
            ]);
            return false;
        }
    } catch (Exception $e) {
        error_log("Telegram notification error: " . $e->getMessage());
        logComplianceEvent('TELEGRAM_FAILED', 0, 'system', [
            'message' => $message,
            'error' => $e->getMessage()
        ]);
        return false;
    }
}

// Send application status update
function sendApplicationStatusUpdate($application_id) {
    global $pdo;

    try {
        $stmt = $pdo->prepare("SELECT a.*, app.name, app.email, v.post_id, p.title as post_title, l.name as lab_name
                              FROM applications a
                              JOIN applicants app ON a.applicant_id = app.id
                              JOIN vacancies v ON a.vacancy_id = v.id
                              JOIN posts p ON v.post_id = p.id
                              JOIN labs l ON v.lab_id = l.id
                              WHERE a.id = ?");
        $stmt->execute([$application_id]);
        $application = $stmt->fetch();

        if (!$application) {
            return false;
        }

        $status_text = [
            'draft' => 'in draft',
            'submitted' => 'submitted',
            'processed' => 'processed',
            'eligible' => 'found eligible',
            'ineligible' => 'found ineligible',
            'selected' => 'selected',
            'rejected' => 'rejected'
        ];

        $subject = "CSIR-SERC Application Update: " . $application['post_title'];
        $message = "Dear " . $application['name'] . ",\n\n";
        $message .= "Your application for the position of " . $application['post_title'] . " at " . $application['lab_name'] . " has been " . $status_text[$application['status']] . ".\n\n";

        if ($application['status'] === 'eligible') {
            $message .= "You have been shortlisted for the next stage of the selection process.\n";
            $message .= "Please keep checking this portal for further updates.\n\n";
        } elseif ($application['status'] === 'ineligible') {
            $message .= "Unfortunately, you do not meet the eligibility criteria for this position.\n";
            $message .= "Please contact recruitment@csir-serc.gov.in for more details.\n\n";
        } elseif ($application['status'] === 'selected') {
            $message .= "Congratulations! You have been selected for the position.\n";
            $message .= "Please find the offer letter attached to this email.\n\n";
        } elseif ($application['status'] === 'rejected') {
            $message .= "Unfortunately, your application has not been successful.\n";
            $message .= "We appreciate your interest in CSIR-SERC.\n\n";
        }

        $message .= "Application ID: APP-" . str_pad($application_id, 6, '0', STR_PAD_LEFT) . "\n";
        $message .= "Status: " . ucfirst($application['status']) . "\n";
        $message .= "Date: " . date('d M Y', strtotime($application['last_updated'])) . "\n\n";
        $message .= "You can view your application status by logging into your account:\n";
        $message .= APP_URL . "/applicant/dashboard.php\n\n";
        $message .= "Thank you,\nCSIR-SERC Recruitment Team";

        // Send email
        $email_sent = sendEmail(
            $application['email'], 
            $subject, 
            $message
        );

        // Send Telegram notification if configured
        $telegram_sent = sendTelegramNotification(
            "Application Status Update\n" .
            "ID: APP-" . str_pad($application_id, 6, '0', STR_PAD_LEFT) . "\n" .
            "Applicant: " . $application['name'] . "\n" .
            "Position: " . $application['post_title'] . "\n" .
            "Status: " . ucfirst($application['status'])
        );

        return $email_sent;
    } catch (Exception $e) {
        error_log("Status update error: " . $e->getMessage());
        return false;
    }
}
