// Create database backup
function createDatabaseBackup() {
    $backup_dir = 'uploads/backups/';
    if (!file_exists($backup_dir)) {
        mkdir($backup_dir, 0755, true);
    }

    $backup_file = $backup_dir . 'csir_serc_recruitment_' . date('Ymd_His') . '.sql';

    $command = sprintf(
        'mysqldump --host=%s --user=%s --password=%s %s > %s',
        escapeshellarg(DB_HOST),
        escapeshellarg(DB_USER),
        escapeshellarg(DB_PASS),
        escapeshellarg(DB_NAME),
        escapeshellarg($backup_file)
    );

    system($command, $return_var);

    if ($return_var === 0 && file_exists($backup_file)) {
        return $backup_file;
    }

    return false;
}

// Create full application backup (DB + documents)
function createFullBackup() {
    $backup_dir = 'uploads/backups/';
    if (!file_exists($backup_dir)) {
        mkdir($backup_dir, 0755, true);
    }

    $timestamp = date('Ymd_His');
    $backup_name = 'csir_serc_recruitment_backup_' . $timestamp;
    $backup_file = $backup_dir . $backup_name . '.zip';

    // Create DB backup
    $db_backup = createDatabaseBackup();
    if (!$db_backup) {
        return false;
    }

    // Create zip archive
    $zip = new ZipArchive();
    if ($zip->open($backup_file, ZipArchive::CREATE) !== TRUE) {
        return false;
    }

    // Add database backup
    $zip->addFile($db_backup, $backup_name . '/database.sql');

    // Add documents
    $zip->addDir('uploads/documents', $backup_name . '/documents');
    $zip->addDir('uploads/photos', $backup_name . '/photos');

    // Add application PDFs
    $zip->addDir('uploads/applications', $backup_name . '/applications');

    $zip->close();

    // Clean up DB backup file
    unlink($db_backup);

    return $backup_file;
}

// Helper function to add directory to zip
if (!function_exists('addDirToZip')) {
    function addDirToZip($dir, $zip, $zipDir = '') {
        $files = new RecursiveIteratorIterator(
            new RecursiveDirectoryIterator($dir),
            RecursiveIteratorIterator::LEAVES_ONLY
        );

        foreach ($files as $name => $file) {
            if (!$file->isDir()) {
                $filePath = $file->getRealPath();
                $relativePath = substr($filePath, strlen($dir) + 1);

                $zip->addFile($filePath, $zipDir . '/' . $relativePath);
            }
        }
    }
}

