// Check if user is eligible for position
function isEligibleForPosition($applicant_id, $vacancy_id) {
    global $pdo;

    try {
        // Get applicant data
        $stmt = $pdo->prepare("SELECT * FROM applicants WHERE id = ?");
        $stmt->execute([$applicant_id]);
        $applicant = $stmt->fetch();

        if (!$applicant) {
            return false;
        }

        // Get vacancy data
        $stmt = $pdo->prepare("SELECT v.*, p.minimum_qualification, p.eligibility_criteria
                              FROM vacancies v
                              JOIN posts p ON v.post_id = p.id
                              WHERE v.id = ?");
        $stmt->execute([$vacancy_id]);
        $vacancy = $stmt->fetch();

        if (!$vacancy) {
            return false;
        }

        // Check age limit
        $dob = new DateTime($applicant['dob']);
        $today = new DateTime();
        $age = $today->diff($dob)->y;

        $max_age = 35; // Default for Scientist posts
        if (strpos($vacancy['post_title'], 'Scientist') !== false) {
            $max_age = 35;
        } elseif (strpos($vacancy['post_title'], 'Technician') !== false) {
            $max_age = 30;
        } elseif (strpos($vacancy['post_title'], 'Technical Assistant') !== false) {
            $max_age = 27;
        }

        if ($age > $max_age) {
            return false;
        }

        // Check educational qualification
        $stmt = $pdo->prepare("SELECT * FROM education WHERE applicant_id = ?");
        $stmt->execute([$applicant_id]);
        $education = $stmt->fetchAll();

        $has_qualification = false;
        foreach ($education as $edu) {
            if (stripos($edu['degree'], $vacancy['minimum_qualification']) !== false) {
                $has_qualification = true;
                break;
            }
        }

        if (!$has_qualification) {
            return false;
        }

        // Check experience if required
        if ($vacancy['experience_required'] > 0) {
            $total_experience = calculateTotalExperience($applicant_id);
            if ($total_experience < $vacancy['experience_required']) {
                return false;
            }
        }

        return true;
    } catch (PDOException $e) {
        error_log("Eligibility check error: " . $e->getMessage());
        return false;
    }
}

// Calculate total work experience for applicant
function calculateTotalExperience($applicant_id) {
    global $pdo;

    try {
        $stmt = $pdo->prepare("SELECT * FROM experience WHERE applicant_id = ?");
        $stmt->execute([$applicant_id]);
        $experience = $stmt->fetchAll();

        $total_months = 0;
        foreach ($experience as $exp) {
            $start = new DateTime($exp['start_date']);
            $end = !empty($exp['end_date']) ? new DateTime($exp['end_date']) : new DateTime();

            $interval = $start->diff($end);
            $total_months += ($interval->y * 12) + $interval->m;
        }

        return round($total_months / 12, 1);
    } catch (PDOException $e) {
        error_log("Experience calculation error: " . $e->getMessage());
        return 0;
    }
}

