// Get applications by status
function getApplicationsByStatus($status = null, $lab_id = null, $post_id = null) {
    global $pdo;

    try {
        $sql = "SELECT a.*, app.name AS applicant_name, app.email, app.phone, 
                p.title AS post_title, l.name AS lab_name, pr.name AS project_name
                FROM applications a
                JOIN applicants app ON a.applicant_id = app.id
                JOIN vacancies v ON a.vacancy_id = v.id
                JOIN posts p ON v.post_id = p.id
                JOIN labs l ON v.lab_id = l.id
                LEFT JOIN projects pr ON v.project_id = pr.id";

        $conditions = [];
        $params = [];

        if ($status) {
            $conditions[] = "a.status = ?";
            $params[] = $status;
        }

        if ($lab_id) {
            $conditions[] = "v.lab_id = ?";
            $params[] = $lab_id;
        }

        if ($post_id) {
            $conditions[] = "v.post_id = ?";
            $params[] = $post_id;
        }

        if (!empty($conditions)) {
            $sql .= " WHERE " . implode(" AND ", $conditions);
        }

        $sql .= " ORDER BY a.submission_date DESC";

        $stmt = $pdo->prepare($sql);
        $stmt->execute($params);
        return $stmt->fetchAll();
    } catch (PDOException $e) {
        error_log("Get applications error: " . $e->getMessage());
        return [];
    }
}

// Process application eligibility
function processApplicationEligibility($application_id) {
    global $pdo;

    try {
        $pdo->beginTransaction();

        // Get application data
        $stmt = $pdo->prepare("SELECT a.*, v.post_id, v.lab_id, p.title as post_title
                              FROM applications a
                              JOIN vacancies v ON a.vacancy_id = v.id
                              JOIN posts p ON v.post_id = p.id
                              WHERE a.id = ?");
        $stmt->execute([$application_id]);
        $application = $stmt->fetch();

        if (!$application) {
            throw new Exception("Application not found");
        }

        // Check eligibility
        $is_eligible = isEligibleForPosition($application['applicant_id'], $application['vacancy_id']);

        // Update application status
        $new_status = $is_eligible ? 'eligible' : 'ineligible';

        $stmt = $pdo->prepare("UPDATE applications SET status = ? WHERE id = ?");
        $stmt->execute([$new_status, $application_id]);

        // Save eligibility details
        $stmt = $pdo->prepare("DELETE FROM eligibility_checks WHERE application_id = ?");
        $stmt->execute([$application_id]);

        if ($is_eligible) {
            $stmt = $pdo->prepare("INSERT INTO eligibility_checks (application_id, check_type, result, details) 
                                  VALUES (?, 'CSIR_GUIDELINE', 'pass', 'Meets all eligibility criteria')");
            $stmt->execute([$application_id]);
        } else {
            // Get specific eligibility issues
            $issues = checkCSIREligibilityForApplicant(
                $application['applicant_id'], 
                $application['vacancy_id']
            );

            foreach ($issues as $issue) {
                $stmt = $pdo->prepare("INSERT INTO eligibility_checks (application_id, check_type, result, details) 
                                      VALUES (?, 'CSIR_GUIDELINE', 'fail', ?)");
                $stmt->execute([$application_id, $issue]);
            }
        }

        $pdo->commit();

        // Send status update
        sendApplicationStatusUpdate($application_id);

        return $is_eligible;
    } catch (Exception $e) {
        $pdo->rollBack();
        error_log("Eligibility processing error: " . $e->getMessage());
        return false;
    }
}

// Check eligibility for specific applicant and vacancy
function checkCSIREligibilityForApplicant($applicant_id, $vacancy_id) {
    global $pdo;

    try {
        // Get applicant data
        $stmt = $pdo->prepare("SELECT * FROM applicants WHERE id = ?");
        $stmt->execute([$applicant_id]);
        $applicant = $stmt->fetch();

        if (!$applicant) {
            throw new Exception("Applicant not found");
        }

        // Get education
        $stmt = $pdo->prepare("SELECT * FROM education WHERE applicant_id = ?");
        $stmt->execute([$applicant_id]);
        $education = $stmt->fetchAll();

        // Get experience
        $stmt = $pdo->prepare("SELECT * FROM experience WHERE applicant_id = ?");
        $stmt->execute([$applicant_id]);
        $experience = $stmt->fetchAll();

        // Get vacancy
        $stmt = $pdo->prepare("SELECT v.*, p.title as post_title, p.minimum_qualification, p.eligibility_criteria, p.experience_required
                              FROM vacancies v
                              JOIN posts p ON v.post_id = p.id
                              WHERE v.id = ?");
        $stmt->execute([$vacancy_id]);
        $vacancy = $stmt->fetch();

        if (!$vacancy) {
            throw new Exception("Vacancy not found");
        }

        return checkCSIREligibility([
            'education' => $education,
            'experience' => $experience
        ], $vacancy);
    } catch (Exception $e) {
        error_log("CSIR eligibility check error: " . $e->getMessage());
        return ["Error checking eligibility: " . $e->getMessage()];
    }
}
```

