  // In application validation
  function validateEligibility($applicant, $vacancy) {
      // Check minimum qualification against CSIR guidelines
      $min_qual = getPostMinimumQualification($vacancy['post_id']);

      if ($applicant['degree'] !== $min_qual) {
          throw new Exception("Minimum qualification requirement not met (CSIR Guidelines Section 5.2)");
      }

      // Check age limit (35 years for Scientist)
      $dob = new DateTime($applicant['dob']);
      $age = $dob->diff(new DateTime())->y;
      if ($age > 35) {
          throw new Exception("Age limit exceeded (CSIR Recruitment Rules)");
      }
  }
