// File upload handler (applicant/upload.php)
if ($_FILES['document']['size'] > 0) {
    $allowed = ['pdf', 'docx', 'jpg', 'jpeg', 'png'];
    $ext = strtolower(pathinfo($_FILES['document']['name'], PATHINFO_EXTENSION));

    if (!in_array($ext, $allowed)) {
        die('Invalid file type');
    }

    $filename = uniqid('doc_') . '.' . $ext;
    $upload_path = 'uploads/documents/' . $filename;

    if (move_uploaded_file($_FILES['document']['tmp_name'], $upload_path)) {
        // Save to database
        $stmt = $pdo->prepare("INSERT INTO documents (application_id, document_type, file_path) VALUES (?, ?, ?)");
        $stmt->execute([$app_id, $_POST['doc_type'], $upload_path]);
    }
}
