<?php
require_once 'db.php';
require_once 'functions.php';
require_once 'compliance.php';

session_set_cookie_params([
    'lifetime' => SESSION_LIFETIME,
    'path' => '/',
    'domain' => parse_url(APP_URL, PHP_URL_HOST),
    'secure' => true,
    'httponly' => true,
    'samesite' => 'Strict'
]);
session_start();

// Regenerate session ID periodically for security
if (!isset($_SESSION['last_regeneration'])) {
    session_regenerate_id(true);
    $_SESSION['last_regeneration'] = time();
} elseif (time() - $_SESSION['last_regeneration'] > 300) {
    session_regenerate_id(true);
    $_SESSION['last_regeneration'] = time();
}

// Check session timeout
if (isset($_SESSION['last_activity']) && (time() - $_SESSION['last_activity'] > SESSION_LIFETIME)) {
    logoutUser();
}

// Update last activity time stamp
$_SESSION['last_activity'] = time();

// CSRF Protection
if (empty($_SESSION['csrf_token'])) {
    $_SESSION['csrf_token'] = bin2hex(random_bytes(32));
}

function checkCsrfToken($token) {
    if (empty($token) || empty($_SESSION['csrf_token'])) {
        return false;
    }
    return hash_equals($_SESSION['csrf_token'], $token);
}

function loginUser($email, $password, $role = 'applicant') {
    global $pdo;

    // Input validation
    if (empty($email) || empty($password)) {
        return ['success' => false, 'message' => 'Email and password are required'];
    }

    // Sanitize email
    $email = filter_var($email, FILTER_SANITIZE_EMAIL);

    // Determine table based on role
    $table = ($role === 'admin') ? 'admins' : 'applicants';

    try {
        $stmt = $pdo->prepare("SELECT * FROM $table WHERE email = ?");
        $stmt->execute([$email]);
        $user = $stmt->fetch();

        if (!$user) {
            return ['success' => false, 'message' => 'Invalid email or password'];
        }

        // Verify password
        if (!password_verify($password, $user['password'])) {
            return ['success' => false, 'message' => 'Invalid email or password'];
        }

        // Check if account is active
        if ($role === 'applicant' && $user['status'] !== 'active') {
            return ['success' => false, 'message' => 'Your account is not active. Please contact support.'];
        }

        // Store user in session
        $_SESSION['user'] = [
            'id' => $user['id'],
            'email' => $user['email'],
            'name' => $user['name'],
            'role' => $role,
            'authenticated' => true
        ];

        // Log login activity for compliance
        logComplianceEvent('LOGIN_SUCCESS', $user['id'], $role);

        return ['success' => true, 'message' => 'Login successful'];
    } catch (PDOException $e) {
        error_log("Database error during login: " . $e->getMessage());
        return ['success' => false, 'message' => 'Database error. Please try again later.'];
    }
}

function logoutUser() {
    // Log logout activity for compliance
    if (isset($_SESSION['user'])) {
        logComplianceEvent('LOGOUT', $_SESSION['user']['id'], $_SESSION['user']['role']);
    }

    // Destroy session
    $_SESSION = [];
    session_unset();
    session_destroy();

    // Delete session cookie
    if (ini_get("session.use_cookies")) {
        $params = session_get_cookie_params();
        setcookie(session_name(), '', time() - 42000,
            $params["path"], $params["domain"],
            $params["secure"], $params["httponly"]
        );
    }

    // Redirect to login page
    header('Location: ' . ($role === 'admin' ? '../admin/login.php' : 'login.php'));
    exit();
}

function requireLogin($role = null) {
    if (!isset($_SESSION['user']) || !$_SESSION['user']['authenticated']) {
        header('Location: ' . ($role === 'admin' ? '../admin/login.php' : 'login.php'));
        exit();
    }

    if ($role && $_SESSION['user']['role'] !== $role) {
        header('HTTP/1.0 403 Forbidden');
        exit('Access denied');
    }
}

function isUserLoggedIn() {
    return isset($_SESSION['user']) && $_SESSION['user']['authenticated'];
}

function getCurrentUser() {
    return isUserLoggedIn() ? $_SESSION['user'] : null;
}

function hashPassword($password) {
    if (strlen($password) < PASSWORD_MIN_LENGTH) {
        return false;
    }
    return password_hash($password, PASSWORD_DEFAULT);
}

// Password policy enforcement
function validatePassword($password) {
    $errors = [];

    if (strlen($password) < PASSWORD_MIN_LENGTH) {
        $errors[] = "Password must be at least " . PASSWORD_MIN_LENGTH . " characters long";
    }

    if (!preg_match('/[A-Z]/', $password)) {
        $errors[] = "Password must contain at least one uppercase letter";
    }

    if (!preg_match('/[a-z]/', $password)) {
        $errors[] = "Password must contain at least one lowercase letter";
    }

    if (!preg_match('/[0-9]/', $password)) {
        $errors[] = "Password must contain at least one number";
    }

    if (!preg_match('/[^A-Za-z0-9]/', $password)) {
        $errors[] = "Password must contain at least one special character";
    }

    return $errors;
}
