    <script>
        // Dynamic vacancy loading based on lab and post selection
        document.addEventListener('DOMContentLoaded', function() {
            const labSelect = document.getElementById('lab_id');
            const postSelect = document.getElementById('post_id');
            const vacancySelect = document.getElementById('vacancy_id');
            const vacancyDetails = document.getElementById('vacancy-details');

            function loadVacancies() {
                const labId = labSelect.value;
                const postId = postSelect.value;

                if (!labId || !postId) {
                    vacancySelect.innerHTML = '<option value="">-- Select Vacancy --</option>';
                    vacancyDetails.classList.add('hidden');
                    return;
                }

                // Show loading state
                vacancySelect.innerHTML = '<option value="">Loading vacancies...</option>';

                // Fetch vacancies via AJAX
                axios.get('<?= APP_URL ?>/api/vacancies.php', {
                    params: {
                        lab_id: labId,
                        post_id: postId
                    }
                })
                .then(function(response) {
                    if (response.data.success && response.data.vacancies.length > 0) {
                        let options = '<option value="">-- Select Vacancy --</option>';

                        response.data.vacancies.forEach(vacancy => {
                            options += `<option value="${vacancy.id}">${vacancy.title} (${vacancy.vacancies} vacancies)</option>`;
                        });

                        vacancySelect.innerHTML = options;
                        vacancyDetails.classList.add('hidden');
                    } else {
                        vacancySelect.innerHTML = '<option value="">No vacancies available</option>';
                        vacancyDetails.classList.add('hidden');
                    }
                })
                .catch(function(error) {
                    console.error('Error loading vacancies:', error);
                    vacancySelect.innerHTML = '<option value="">Error loading vacancies</option>';
                });
            }

            // Event listeners
            labSelect.addEventListener('change', loadVacancies);
            postSelect.addEventListener('change', loadVacancies);

            // Vacancy selection details
            vacancySelect.addEventListener('change', function() {
                const selectedOption = vacancySelect.options[vacancySelect.selectedIndex];

