                        params: {
                            id: this.value
                        }
                    })
                    .then(function(response) {
                        if (response.data.success && response.data.vacancy) {
                            const vacancy = response.data.vacancy;
                            document.getElementById('vacancy-position').textContent = vacancy.post_title;
                            document.getElementById('vacancy-lab').textContent = vacancy.lab_name;
                            document.getElementById('vacancy-project').textContent = vacancy.project_name || 'N/A';
                            document.getElementById('vacancy-count').textContent = vacancy.vacancies;
                            document.getElementById('vacancy-deadline').textContent = formatDate(vacancy.deadline);
                            document.getElementById('vacancy-eligibility').textContent = vacancy.eligibility_criteria;
                            vacancyDetails.classList.remove('hidden');
                        }
