// Send email using PHPMailer
function sendEmail($to, $subject, $body, $attachments = []) {
    // Only send if SMTP settings are configured
    if (empty(SMTP_HOST) || empty(SMTP_USER)) {
        error_log("Email not sent: SMTP not configured");
        return false;
    }

    try {
        require 'vendor/phpmailer/phpmailer/src/PHPMailer.php';
        require 'vendor/phpmailer/phpmailer/src/SMTP.php';
        require 'vendor/phpmailer/phpmailer/src/Exception.php';

        $mail = new PHPMailer\PHPMailer\PHPMailer(true);

        // Server settings
        $mail->isSMTP();
        $mail->Host = SMTP_HOST;
        $mail->Port = SMTP_PORT;
        $mail->SMTPAuth = true;
        $mail->Username = SMTP_USER;
        $mail->Password = SMTP_PASS;
        $mail->SMTPSecure = PHPMailer\PHPMailer\PHPMailer::ENCRYPTION_STARTTLS;

        // Content
        $mail->isHTML(true);
        $mail->CharSet = 'UTF-8';

        // Add headers for government compliance
        $mail->addCustomHeader('X-CSIR-Department', 'SERC Recruitment');
        $mail->addCustomHeader('X-CSIR-GUIDELINES', CSIR_GUIDELINES_VERSION);
        $mail->addCustomHeader('X-GIGW-COMPLIANT', GIGW_COMPLIANT ? 'true' : 'false');

        // From
        $mail->setFrom(SMTP_FROM, SMTP_NAME);

        // To
        $mail->addAddress($to);

        // Subject
        $mail->Subject = $subject;

        // Body
        $mail->Body = generateEmailTemplate($subject, $body);

        // Attachments
        foreach ($attachments as $attachment) {
            $mail->addAttachment($attachment['path'], $attachment['name']);
        }

        // Send
        $result = $mail->send();

        // Log for compliance
        logComplianceEvent('EMAIL_SENT', 0, 'system', [
            'to' => $to,
            'subject' => $subject,
            'success' => true
        ]);

        return true;
    } catch (Exception $e) {
        error_log("Email sending failed: " . $e->errorMessage());

        // Log failure for compliance
        logComplianceEvent('EMAIL_FAILED', 0, 'system', [
            'to' => $to,
            'subject' => $subject,
            'error' => $e->getMessage()
        ]);

        return false;
    }
}

