CREATE TRIGGER `update_last_activity` BEFORE UPDATE ON `applicants`
FOR EACH ROW
BEGIN
    IF OLD.status != NEW.status THEN
        SET NEW.updated_at = CURRENT_TIMESTAMP;
    END IF;
END$$
