// Get dashboard statistics
function getDashboardStats() {
    global $pdo;

    try {
        $stats = [
            'total_vacancies' => 0,
            'total_applications' => 0,
            'pending_applications' => 0,
            'eligible_applications' => 0,
            'ineligible_applications' => 0,
            'processing_applications' => 0,
            'selected_applications' => 0,
            'rejected_applications' => 0
        ];

        // Total vacancies
        $stmt = $pdo->query("SELECT COUNT(*) FROM vacancies WHERE status = 'open'");
        $stats['total_vacancies'] = $stmt->fetchColumn();

        // Total applications
        $stmt = $pdo->query("SELECT COUNT(*) FROM applications");
        $stats['total_applications'] = $stmt->fetchColumn();

        // Applications by status
        $statuses = [
            'pending' => 'submitted',
            'eligible_applications' => 'eligible',
            'ineligible_applications' => 'ineligible',
            'processing_applications' => 'processed',
            'selected_applications' => 'selected',
            'rejected_applications' => 'rejected'
        ];

        foreach ($statuses as $stat_key => $status) {
            $stmt = $pdo->prepare("SELECT COUNT(*) FROM applications WHERE status = ?");
            $stmt->execute([$status]);
            $stats[$stat_key] = $stmt->fetchColumn();
