}

// Create database backup
function createDatabaseBackup() {
    $backup_dir = 'uploads/backups/';
    if (!file_exists($backup_dir)) {
        mkdir($backup_dir, 0755, true);
    }

    $backup_file = $backup_dir . 'csir_serc_recruitment_' . date('Ymd_His') . '.sql';

    $command = sprintf(
        'mysqldump --host=%s --user=%s --password=%s %s > %s',
        escapeshellarg(DB_HOST),
        escapeshellarg(DB_USER),
        escapeshellarg(DB_PASS),
        escapeshellarg(DB_NAME),
        escapeshellarg($backup_file)
    );

    system($command, $return_var);

    if ($return_var === 0 && file_exists($backup_file)) {
        return $backup_file;
    }

    return false;
}

// Create full application backup (DB + documents)
function createFullBackup() {
    $backup_dir = 'uploads/backups/';
    if (!file_exists($backup_dir)) {
        mkdir($backup_dir, 0755, true);
    }

    $timestamp = date('Ymd_His');
    $backup_name = 'csir_serc_recruitment_backup_' . $timestamp;
    $backup_file = $backup_dir . $backup_name . '.zip';

    // Create DB backup
    $db_backup = createDatabaseBackup();
    if (!$db_backup) {
        return false;
    }

    // Create zip archive
    $zip = new ZipArchive();
    if ($zip->open($backup_file, ZipArchive::CREATE) !== TRUE) {
        return false;
    }

    // Add database backup
    $zip->addFile($db_backup, $backup_name . '/database.sql');

    // Add documents
    $zip->addDir('uploads/documents', $backup_name . '/documents');
    $zip->addDir('uploads/photos', $backup_name . '/photos');

    // Add application PDFs
    $zip->addDir('uploads/applications', $backup_name . '/applications');

    $zip->close();

    // Clean up DB backup file
    unlink($db_backup);

    return $backup_file;
}

// Helper function to add directory to zip
if (!function_exists('addDirToZip')) {
    function addDirToZip($dir, $zip, $zipDir = '') {
        $files = new RecursiveIteratorIterator(
            new RecursiveDirectoryIterator($dir),
            RecursiveIteratorIterator::LEAVES_ONLY
        );

        foreach ($files as $name => $file) {
            if (!$file->isDir()) {
                $filePath = $file->getRealPath();
                $relativePath = substr($filePath, strlen($dir) + 1);

                $zip->addFile($filePath, $zipDir . '/' . $relativePath);
            }
        }
    }
}

// Sanitize input
function sanitizeInput($data) {
    if (is_array($data)) {
        return array_map('sanitizeInput', $data);
    }
    return htmlspecialchars(trim($data));
}

// Check if user is eligible for position
function isEligibleForPosition($applicant_id, $vacancy_id) {
    global $pdo;

    try {
        // Get applicant data
        $stmt = $pdo->prepare("SELECT * FROM applicants WHERE id = ?");
        $stmt->execute([$applicant_id]);
        $applicant = $stmt->fetch();

        if (!$applicant) {
            return false;
        }

        // Get vacancy data
        $stmt = $pdo->prepare("SELECT v.*, p.minimum_qualification, p.eligibility_criteria
                              FROM vacancies v
                              JOIN posts p ON v.post_id = p.id
                              WHERE v.id = ?");
        $stmt->execute([$vacancy_id]);
        $vacancy = $stmt->fetch();

        if (!$vacancy) {
            return false;
        }

        // Check age limit
        $dob = new DateTime($applicant['dob']);
        $today = new DateTime();
        $age = $today->diff($dob)->y;

        $max_age = 35; // Default for Scientist posts
        if (strpos($vacancy['post_title'], 'Scientist') !== false) {
            $max_age = 35;
        } elseif (strpos($vacancy['post_title'], 'Technician') !== false) {
            $max_age = 30;
        } elseif (strpos($vacancy['post_title'], 'Technical Assistant') !== false) {
            $max_age = 27;
        }

        if ($age > $max_age) {
            return false;
        }

        // Check educational qualification
        $stmt = $pdo->prepare("SELECT * FROM education WHERE applicant_id = ?");
        $stmt->execute([$applicant_id]);
        $education = $stmt->fetchAll();

        $has_qualification = false;
        foreach ($education as $edu) {
            if (stripos($edu['degree'], $vacancy['minimum_qualification']) !== false) {
                $has_qualification = true;
                break;
            }
        }

        if (!$has_qualification) {
            return false;
        }

        // Check experience if required
        if ($vacancy['experience_required'] > 0) {
            $total_experience = calculateTotalExperience($applicant_id);
            if ($total_experience < $vacancy['experience_required']) {
                return false;
            }
        }

        return true;
    } catch (PDOException $e) {
        error_log("Eligibility check error: " . $e->getMessage());
        return false;
    }
}

// Calculate total work experience for applicant
function calculateTotalExperience($applicant_id) {
    global $pdo;

    try {
        $stmt = $pdo->prepare("SELECT * FROM experience WHERE applicant_id = ?");
        $stmt->execute([$applicant_id]);
        $experience = $stmt->fetchAll();

        $total_months = 0;
        foreach ($experience as $exp) {
            $start = new DateTime($exp['start_date']);
            $end = !empty($exp['end_date']) ? new DateTime($exp['end_date']) : new DateTime();

            $interval = $start->diff($end);
            $total_months += ($interval->y * 12) + $interval->m;
        }

        return round($total_months / 12, 1);
    } catch (PDOException $e) {
        error_log("Experience calculation error: " . $e->getMessage());
        return 0;
    }
}

